// This file is automatically generated. Do not edit it directly.
import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';

const SUPABASE_URL = import.meta.env.VITE_SUPABASE_URL;
const SUPABASE_PUBLISHABLE_KEY = import.meta.env.VITE_SUPABASE_PUBLISHABLE_KEY;

// Import the supabase client like this:
// import { supabase } from "@/integrations/supabase/client";

export const supabase = createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY, {
  auth: {
    storage: localStorage,
    persistSession: true,
    autoRefreshToken: true,
  },
  global: {
    headers: {
      'X-Client-Info': 'martech-efi'
    }
  },
  db: {
    schema: 'public'
  },
  realtime: {
    params: {
      eventsPerSecond: 10
    }
  }
});

// Listeners de eventos de autentica√ß√£o para debug e gerenciamento
supabase.auth.onAuthStateChange((event, session) => {
  console.log(`üîê [Supabase] Auth event: ${event}`, {
    userId: session?.user?.id?.substring(0, 8),
    expiresAt: session?.expires_at ? new Date(session.expires_at * 1000).toISOString() : null
  });

  // Verificar se o token est√° perto de expirar (< 5 minutos) e renovar proativamente
  if (session?.expires_at) {
    const expiresAt = session.expires_at * 1000; // Converter para ms
    const now = Date.now();
    const timeUntilExpiry = expiresAt - now;
    const fiveMinutes = 5 * 60 * 1000;

    if (timeUntilExpiry > 0 && timeUntilExpiry < fiveMinutes) {
      console.log('‚ö†Ô∏è [Supabase] Token expirando em breve, renovando proativamente...');
      supabase.auth.refreshSession().then(({ error }) => {
        if (error) {
          console.error('‚ùå [Supabase] Erro ao renovar token proativamente:', error);
        } else {
          console.log('‚úÖ [Supabase] Token renovado proativamente');
        }
      });
    }
  }
});